import { Star } from "lucide-react"
import { Card, CardContent } from "@/components/ui/card"
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import { useEffect, useRef } from "react"

const testimonials = [
  {
    name: "James Otieno",
    role: "Contractor, Nairobi",
    image: "data:image/jpeg;base64,",
    rating: 5,
    quote:
      "Constructly transformed how I run my projects. I can now generate quotes, track tasks, and manage teams from one place!",
  },
  {
    name: "Mary Wanjiru",
    role: "Site Engineer, Kisumu",
    image: "data:image/jpeg;base64,",
    rating: 4,
    quote:
      "It's simplified costing and eliminated guesswork. I love the user-friendly interface and accurate calculations.",
  },
  {
    name: "Kevin Mwangi",
    role: "Quantity Surveyor, Eldoret",
    image: "data:image/jpeg;base64,",
    rating: 5,
    quote:
      "Constructly is the future of construction management in Kenya. Highly recommend it to all professionals!",
  },
  {
    name: "Fatma Yusuf",
    role: "Architect, Mombasa",
    image: "data:image/jpeg;base64,",
    rating: 5,
    quote:
      "Clean UI, reliable performance. Constructly has saved me hours each week. Every professional should try it!",
  },
  {
    name: "Peter Njoroge",
    role: "Foreman, Nakuru",
    image: "data:image/jpeg;base64,",
    rating: 4,
    quote:
      "Simple to use but incredibly powerful. I especially love the mobile compatibility for on-site updates.",
  },
]

export function TestimonialsSection() {
  const scrollRef = useRef<HTMLDivElement>(null)

  useEffect(() => {
    const container = scrollRef.current
    if (!container) return

    let scrollAmount = 0
    const scrollStep = 1
    const maxScroll = container.scrollWidth - container.clientWidth

    const scrollInterval = setInterval(() => {
      scrollAmount += scrollStep
      if (scrollAmount >= maxScroll) {
        scrollAmount = 0
        container.scrollLeft = 0
      } else {
        container.scrollLeft = scrollAmount
      }
    }, 20)

    return () => clearInterval(scrollInterval)
  }, [])

  return (
    <section className=" bg-slate-100 bg-transparent" id="testimonials">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
        <p className="text-muted-foreground mb-12 max-w-xl mx-auto">
          Hear from real users who rely on Constructly to power their construction businesses.
        </p>

        <div
          ref={scrollRef}
          className="flex space-x-6 overflow-x-auto scrollbar-hide px-1 py-4"
        >
          {testimonials.map((t, i) => (
            <Card
              key={i}
              className="min-w-[300px] max-w-xs bg-white dark:bg-slate-800 shadow-md rounded-2xl p-6 flex-shrink-0"
            >
              <CardContent className="flex flex-col justify-between h-full">
                <div className="mb-4 text-left text-muted-foreground italic">
                  “{t.quote}”
                </div>
                <div className="flex items-center mt-6 space-x-4">
                  <Avatar>
                    <AvatarImage src={t.image} alt={t.name} />
                    <AvatarFallback>{t.name.slice(0, 2)}</AvatarFallback>
                  </Avatar>
                  <div>
                    <p className="font-semibold text-left">{t.name}</p>
                    <p className="text-xs text-muted-foreground text-left">{t.role}</p>
                  </div>
                </div>
                <div className="flex mt-4 space-x-1">
                  {[...Array(5)].map((_, j) => (
                    <Star
                      key={j}
                      className={`w-4 h-4 ${
                        j < t.rating ? "text-secondary" : "text-muted"
                      }`}
                      fill={j < t.rating ? "currentColor" : "none"}
                    />
                  ))}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </section>
  )
}
